import React, { Component } from "react";
import { connect } from "react-redux";
import "react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";
import BootstrapTable from "react-bootstrap-table-next";
import axios from "axios";
import { TestFunction } from "./actions/allActions";
import JITHeader from "./JITHeader";
import "react-responsive-modal/styles.css";
import { Modal } from "react-responsive-modal";
//import JSONPretty from "react-json-prettify";
//import { github } from "react-json-prettify/dist/themes";
import JSONPretty from "react-json-pretty";
import "react-json-pretty/themes/monikai.css";
import "./GetCollection.css";

class GetCollection extends Component {
  componentDidMount() {
    this.props.TestFunction();
  }
  constructor(props) {
    super(props);
    this.state = {
      schema: "Mine",
      open: false,
      selectedFile: null,
      apiData: [],
      selectRowProp: {
        mode: "radio",
        clickToSelect: true,
        style: { background: "green" },
        onSelect: (row, isSelect, rowIndex, e) => {
          console.log(row);
          this.onOpenModal(row);
        },
      },
      columns: [
        {
          dataField: "apiType",
          text: "API TYPE",
        },
        {
          dataField: "apiName",
          text: "API NAME",
        },
        {
          dataField: "apiUrl",
          text: "API URL",
        },
      ],
    };
  }
  onChangeHandler = (event) => {
    this.setState({
      selectedFile: event.target.files[0],
      loaded: 0,
    });
  };

  onOpenModal = (row) => {
    this.setState({ open: true, schema: JSON.stringify(row.apiReponseSchema) });
    console.log("OpenModel");
    console.log(row.apiReponseSchema);
  };

  onCloseModal = () => {
    this.setState({ open: false });
  };

  onClickHandler = () => {
    const data = new FormData();
    data.append("file", this.state.selectedFile);
    axios
      .post("http://127.0.0.1:5000/upload", data, {
        // receive two    parameter endpoint url ,form data
      })
      .then((res) => {
        // then print response status
        console.log(res);
        this.setState({
          apiData: res.data,
        });
      });
  };
  render() {
    const { testData } = this.props;
    return (
      <div>
        <JITHeader subHeading="Get Apis" />
        <div class="container">
          <div class="row">
            <div class="col-md-3"></div>
            <div class="col-md-6">
              <form method="post" action="#" id="#">
                <div class="form-group files">
                  <label>Upload Your Postman Collection File </label>
                  <input
                    type="file"
                    class="form-control"
                    onChange={this.onChangeHandler}
                    multiple=""
                  />
                </div>
              </form>
            </div>
            <button
              type="button"
              class="btn btn-success btn-block"
              onClick={this.onClickHandler}
            >
              Upload
            </button>
          </div>
          <BootstrapTable
            keyField="id"
            data={this.state.apiData}
            columns={this.state.columns}
            selectRow={this.state.selectRowProp}
          />
          <div>
            <Modal
              open={this.state.open}
              onClose={this.onCloseModal}
              center
              classNames={{
                overlay: "customOverlay",
                modal: "customModal",
              }}
            >
              <JSONPretty
                id="json-pretty"
                data={this.state.apiData}
              ></JSONPretty>
            </Modal>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  testData: state.dataFromReducer.TestDataInAllReducers,
});

export default connect(mapStateToProps, { TestFunction })(GetCollection);
