import React, { Component } from "react";
import { connect } from "react-redux";
import "react-bootstrap-table-next/dist/react-bootstrap-table2.min.css";
import BootstrapTable from "react-bootstrap-table-next";
import TableHeaderColumn from "react-bootstrap-table-next";
import "./GetCollection.css";
import axios from "axios";
import { TestFunction } from "./actions/allActions";
import JITHeader from "./JITHeader";
const selectRowProp = {
  mode: "radio",
};
class GetCollection extends Component {
  products = [
    {
      id: 1,
      name: "Item name 1",
      price: 100,
    },
    {
      id: 2,
      name: "Item name 2",
      price: 100,
    },
  ];
  componentDidMount() {
    this.props.TestFunction();
  }
  constructor(props) {
    super(props);
    this.state = {
      selectedFile: null,
    };
  }
  onChangeHandler = (event) => {
    this.setState({
      selectedFile: event.target.files[0],
      loaded: 0,
    });
  };

  onClickHandler = () => {
    const data = new FormData();
    data.append("file", this.state.selectedFile);
    axios
      .post("http://127.0.0.1:5000/upload", data, {
        // receive two    parameter endpoint url ,form data
      })
      .then((res) => {
        // then print response status
        console.log(res);
      });
  };
  render() {
    const { testData } = this.props;
    return (
      <div>
        <JITHeader subHeading="Get Apis" />
        <div class="container">
          <div class="row">
            <div class="col-md-3"></div>
            <div class="col-md-6">
              <form method="post" action="#" id="#">
                <div class="form-group files">
                  <label>Upload Your Postman Collection File </label>
                  <input
                    type="file"
                    class="form-control"
                    onChange={this.onChangeHandler}
                    multiple=""
                  />
                </div>
              </form>
            </div>
            <button
              type="button"
              class="btn btn-success btn-block"
              onClick={this.onClickHandler}
            >
              Upload
            </button>
          </div>
          <BootstrapTable data={products} selectRow={selectRowProp}>
            <TableHeaderColumn dataField="id">Product ID</TableHeaderColumn>
            <TableHeaderColumn dataField="name">Product Name</TableHeaderColumn>
            <TableHeaderColumn dataField="price">
              Product Price
            </TableHeaderColumn>
          </BootstrapTable>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  testData: state.dataFromReducer.TestDataInAllReducers,
});

export default connect(mapStateToProps, { TestFunction })(GetCollection);
